mapper.select:
SELECT db.diary_post_id, db.diary_id, db.diary_title, db.diary_content, db.diary_create, db.diary_category, db.diary_views, db.diary_public
FROM diaryboard db
INNER JOIN diary d ON db.diary_id = d.diary_id
WHERE d.user_id = #{userId}
ORDER BY db.diary_create DESC
LIMIT #{offset}, #{size}

SELECT db.diary_post_id, db.diary_id, db.diary_title, db.diary_create, db.diary_category, db.diary_views, db.diary_public FROM diaryboard db INNER JOIN diary d ON db.diary_id = d.diary_id WHERE d.user_id = #{userId}
AND db.diary_public IN ('전체공개', '친구공개')
AND db.diary_public = '전체공개' ORDER BY db.diary_create DESC
